import React,{useState} from "react";

setInterval(updateTime, 1000);

const now = new Date().toDateString();

const [date, setDate] = useState(now);

function updateTime(){
    const newTime = new Date().toDateString();
    setDate(newTime);
}

function Date(){
    return(
        <div className="date">
<h1>{date}</h1>
<button onClick={updateTime}>Get Date</button>
        </div>
    );
}

export default Date;
