function updateTime(){
 const newTime = new Date().toLocaleDateString();
 setTime(newTime);
}

    return(
        <div className="date">
<h1>{time}</h1>
<button onClick={updateTime}>Get Date</button>
        </div>
    );
}

export default Date;
