import React,{useState} from "react";


const now = new Date().toLocaleDateString();

const [date, setTime] = useState(now);

function updateTime(){
 const newTime = new Date().toLocaleDateString();
 setTime(newTime);
}
function Date(){
    return(
        <div className="date">
<h1>{date}</h1>
<button onClick={updateTime}>Get Date</button>
        </div>
    );
}

export default Date;
